mapper.sql:
select *
from field_order as fo
inner join field_rent as fr on fo.rent_id = fr.id


        and fo.id = #{param.id}


        and fo.rent_id = #{param.rentId}


        and fo.stu_number = #{param.stuNumber}


        and fo.status = #{param.status}


        and FIND_IN_SET(#{param.manager},fr.manager)


        and unix_timestamp(fo.start_time)   >=  unix_timestamp(#{param.startTime,jdbcType=VARCHAR})


        and unix_timestamp(fo.end_time)   <=  unix_timestamp(#{param.endTime,jdbcType=VARCHAR})


order by fo.create_time

select * from field_order
and rent_id = #{param.rentId}
and status = #{param.status}
and (unix_timestamp(start_time) <= unix_timestamp(#{param.startTime,jdbcType=VARCHAR})
and unix_timestamp(end_time) >= unix_timestamp(#{param.startTime,jdbcType=VARCHAR})
or unix_timestamp(start_time) <= unix_timestamp(#{param.endTime,jdbcType=VARCHAR}) and unix_timestamp(end_time) >= unix_timestamp(#{param.endTime,jdbcType=VARCHAR}))
